import React from 'react'
import {useState} from 'react'

const tasks=[
    {
        id :1,
        text : 'Doctor',
        day : '5th august at 2:30pm ' ,
        reminder : true,
    },
    {
        id :2,
        text : 'Professor',
        day : '6th august at 5:30pm ' ,
        reminder : true,
    },
    {
        id :3,
        text : 'Shooping',
        day : '6th august at 7:30pm ' ,
        reminder : true,
    }
]

const Tasks = () => {
  const [tasks , setTasks] = use
  return ( 
    <>
    {tasks.map((task) => (
        <h3 key = {task.id}>{task.text}</h3>
      ))}
    </>
  )
}

export default Tasks
